    console.log("Retrieving data from my contract!");
  }
  return (
    <div className="App">
      <header className="App-header">Carbonyte Project</header>
      <div>
        <div className="row">
          <div className="column">
            <h2>My Helper Contract</h2>
            <button className="button" onClick={handleDecide}>Decide</button><br></br>
            <p6>Result: {result}</p6>
          </div>
          <div className="vl"></div>
          <div className="column">Your Arbitrum Contract</div>
        </div>
      </div>
    </div>
  );
}

export default App;
