import logo from './logo.svg';
import './App.css';
import React, {useState} from "react";
import ArbitrumABI from "./ArbitrumABI.json";
import {ethers, BigNumber, utils} from "ethers";




function App() {

  const contractAddress = "0x18B7DFF2a1EC88790D129719232FC07e50FC29A5";
  const [result, setResult] = useState("Not retrieved yet!");
  const [Wins, setWins] = useState("not fetched yet!");

  const handleDecide = () => {
    console.log("Retrieving data from my contract!");
  }

  const handleWalletConnect = async () => {
    if (window.ethereum) {
      window.ethereum.request({ method: "eth_requestAccounts" }).then(result => console.log("Your Public Key:",result[0]));

      const provider = new ethers.providers.Web3Provider(window.ethereum);
      const signer = provider.getSigner();
      const instance = new ethers.Contract(contractAddress, ArbitrumABI.abi, provider);

      const response = await instance.consecutiveWins();
      setWins(response);

    } else {
      alert("Need to install Metamask!");
  }
  }
  return (
    <div className="App">
      <header className="App-header">Carbonyte Project</header>
      <button className="button" onClick={handleWalletConnect}>Connect Wallet</button>
      <div>
        <div className="row">

          <div className="column">
            <h4>My Helper Contract</h4>
            <h6>Result: {result}</h6>
            <button className="button" onClick={handleDecide}>Decide</button>
          </div>

          <div className="vl"></div>

          <div className="column">
            <h4>Your Arbitrum Contract</h4>
            <h6>Consecutive Wins: {consecutiveWins}</h6>
            <button className="button">Send True</button>
            <button className="button">Send False</button>
          </div>

        </div>
      </div>
    </div>
  );
}

export default App;
